import { Component, OnDestroy, OnInit } from "@angular/core";
import { BehaviorSubject, debounceTime, Subscription } from "rxjs";
import { HttpService, Pokemon } from "../services/http/http.service";

@Component({
  selector: "app-main",
  templateUrl: "./main.component.html",
  styleUrls: ["./main.component.scss"],
})
export class MainComponent implements OnInit, OnDestroy {
  pokemons$: BehaviorSubject<Pokemon[]> = this.httpService.getItems$();
  loading$: BehaviorSubject<boolean> = this.httpService.getLoadingState();
  constructor(private httpService: HttpService) {}
  subscriptions: Subscription[] = [];

  ngOnInit(): void {
    let subscription1 = this.httpService.loadPokemons().subscribe();
    let subscription2 = this.pokemons$
      .pipe(debounceTime(500))
      .subscribe((value) => console.log(value));
    this.subscriptions.push(subscription1);
    this.subscriptions.push(subscription2);
  }

  ngOnDestroy(): void {
    this.subscriptions.forEach((item) => item.unsubscribe());
  }
}
<app-header></app-header>
<div class="main__content" *ngIf="loading$ | async as state; else items">
  <app-spiner></app-spiner>
</div>
<ng-template #items>
  <ul class="main__list">
    <li class="main__list-item" *ngFor="let item of pokemons$ | async">
      <img
        src="https://unpkg.com/pokeapi-sprites@2.0.4/sprites/pokemon/other/dream-world/{{
          item.id
        }}.svg"
        [alt]="item.name"
        class="main__img"
      />
      <div class="main__box">
        <p class="main_text">{{ item.name }}</p>
        <ul class="main_sublist">
          <li class="main__sublist-item" *ngFor="let item of item.types">
            {{ item.type.name }}
          </li>
        </ul>
      </div>
    </li>
  </ul>
</ng-template>
